def gen(n, slist):
    reviews = []
    rating_sum = 0
    
    for _ in range(n):
        username = ''.join(map(lambda s: s.title(), random.sample(slist, 2)))
        title = ''.join(random.sample(slist, random.randint(5, 10)))
        body = ',\n            '.join(f'"{"".join(random.sample(slist, random.randint(5, 20))).capitalize()}"' for _ in range(random.randint(2, 5)))
        date = datetime.fromtimestamp(random.randint(1610069884, 1641604037)).isoformat()
        rating = random.randint(1, 10) / 2 if random.random() < .1 else random.randint(8, 10) / 2

        rating_sum += rating


        reviews.append(f'''  {{
    "username": "{username}",
        "title": "{title}",
        "body": [
            {body}
        ],
        "date": "{date}",
        "rating": {rating}
    }}''')
    reviews_str = ',\n'.join(reviews)
    
    print(f'''
"reviews": [
{reviews_str}
]
''', end='\n\n')

    print("Summary:")
    print("Average rating:")
    print(round(rating_sum / n, 2))
    print("Ratings number:")
    print(n)

gen(11, s.split())